import React, { useState, useEffect } from 'react';

const Contact= () => {
  const [notes, setNotes] = useState([]);
  const [newNote, setNewNote] = useState({ title: '', content: '' });

  // Retrieve user ID from local storage
  const userInfo = JSON.parse(localStorage.getItem('user_info'));
  const user_id = userInfo?.user_id;
  console.log(userInfo)

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewNote({ ...newNote, [name]: value });
  };

  const saveNote = async () => {
    // Ensure user_id is not null before making the request
    if (!user_id) {
      console.error('User ID is null. Unable to save note.');
      return;
    }

    try {
      // Make sure you include user_id in the request payload
      const response = await fetch('http://localhost:2002/api/notes', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          user_id: user_id,
          title: newNote.title,
          content: newNote.content,
        }),
      });

      const data = await response.json();
      setNotes([...notes, data]);
      setNewNote({ title: '', content: '' });
    } catch (error) {
      console.error('Error saving note:', error);
    }
  };

  useEffect(() => {
    console.log('New Note:', newNote);
  }, [newNote]);

  return (
    <div className="contact">
      <div className="contactm">
        <h2>Write any massage for Me! :</h2>
        <label>your email:</label>
        <input
          type="text"
          name="title"  // Add name attribute
          value={newNote.email}
          onChange={handleInputChange}  // Use handleInputChange for onChange
        />
        <label>Title:</label>
        <input
          type="text"
          name="title"  // Add name attribute
          value={newNote.title}
          onChange={handleInputChange}  // Use handleInputChange for onChange
        />
        <label>Content:</label>
        <textarea
          name="content"  // Add name attribute
          value={newNote.content}
          onChange={handleInputChange}  // Use handleInputChange for onChange
        />
        <button onClick={saveNote}>send</button>

      </div>
      <div className='contactid'>
      <div className="box">
      <h3>Contact Info</h3>
      <p>
        <i className="fas fa-phone"></i>+91 1387242785
      </p>
      <p>
        <i className="fas fa-envelope"></i>email: f1stmain601@gmail.com
      </p>
      <p>
        <i className="fas fa-map-marked-alt"></i>location: Gilgit, Pakistan
      </p>
        <a
          href="https://www.linkedin.com/in/muhammada1y"
          className="fab fa-linkedin"
          aria-label="LinkedIn"
          target="_blank"
          rel="noopener noreferrer"
        ></a>
        <a
          href="https://github.com/muhammada1y"
          className="fab fa-github"
          aria-label="GitHub"
          target="_blank"
          rel="noopener noreferrer"
        ></a>
        <a
          href="mailto:muhammad.ali.cloudlem@gmail.com"
          className="fas fa-envelope"
          aria-label="Mail"
          target="_blank"
          rel="noopener noreferrer"
        ></a>
        <a
          href="https://twitter.com/muhamma70850981"
          className="fab fa-twitter"
          aria-label="Twitter"
          target="_blank"
          rel="noopener noreferrer"
        ></a>
      </div>
      </div>
    </div>
  );
};

export default Contact;
